import React, { useEffect, useState } from "react";
import ReactDOM from "react-dom";
import { Slide, Fade } from "react-slideshow-image";
import "react-slideshow-image/dist/styles.css";
export default function SlideImgHomepage() {
  const [widthOfDevice, setWidthOfDevice] = useState("");
  const imgSrc = ["img/bg1.jpg", "img/bg2.jpg", "img/bg3.jpg"];
  const slideImgContents = [
    ["DISCOVER", '"LE"', "BIKE"],
    ["DISCOVER", '"LE"', "BIKE"],
    ["DISCOVER", '"LE"', "BIKE"],
  ];
  const properties = {
    duration: 3500,
    transitionDuration: 500,
    infinite: true,
    indicators: true,
    canSwipe: true,
    scale: 0.4,
    pauseOnHover: false,
    indicators: (i) => <div className="indicator"></div>,
    prevArrow: (
      <div
        style={{
          width: "300px",
          marginRight: "0",
          position: "absolute",
          left: 0,
          height: "100vh",
          display: "none",
        }}
      ></div>
    ),
    nextArrow: (
      <div
        style={{
          width: "300px",
          marginRight: "0",
          position: "absolute",
          right: 0,
          height: "100vh",
          display: "none",
        }}
      ></div>
    ),
  };

  useEffect(() => {
    ReactDOM.render(<SlideImgHomepage />, document.getElementById("main-bg"));
  }, [widthOfDevice]);
  window.addEventListener("resize", reportWindowSize);
  function reportWindowSize() {
    setWidthOfDevice(window.innerWidth);
  }
  if (window.outerWidth > 750) {
    return (
      <>
        <div style={{ transition: "1s ease" }}>
          <Fade {...properties}>
            {imgSrc.map((item, index) => (
              <div className="each-fade">
                <div>
                  <div className="slideImgContentLayout">
                    <div className="slideImgContentLayout_top">
                      <img className="img-bg-title" alt="" src="./img/homepage/sv-alpine-logo.svg"></img>
                    </div>
                    <div className="slideImgContentLayout_center">
                      {slideImgContents[index].map((slideImgContent, key)=>(
                        <span>{slideImgContent}</span>
                      ))}
                      <button type="button"><a>LEARN MORE</a></button>
                    </div>
                  </div>
                  <img src={imgSrc[index]} />
                </div>
              </div>
            ))}
          </Fade>
        </div>
      </>
    );
  } else {
    return (
      <>
        <div
          style={{
            width: "100%",
            height: "100%",
            backgroundImage: `url(./${imgSrc[0]})`,
          }}
        ></div>
      </>
    );
  }
}
