import React, { useEffect, useState } from "react";
import { Fade, Slide } from "react-slideshow-image";
import "react-slideshow-image/dist/styles.css";

export default function MotorSlideImg({imgSlideSrc}) {
  const [size, setSize] = useState(window.outerWidth > 850 ? false : true);
  window.onresize = () => {
    if (window.outerWidth > 850) {
      setSize(false);
    } else {
      setSize(true);
    }
  };
  const properties = {
    autoplay: false,
    duration: 3500,
    transitionDuration: 500,
    infinite: true,
    indicators: false,
    canSwipe: size,
    scale: 0.4,
    pauseOnHover: false,
    indicators: (i) => <div className="indicator"></div>,
    prevArrow: (
      <div
        style={{
          width: "15%",
          marginRight: "0",
          position: "absolute",
          right: "100%",
          height: "100vh",
          backgroundImage: "url(./img/left-arrow.png)",
          backgroundPosition: "center",
          backgroundSize: "contain",
        }}
      ></div>
    ),
    nextArrow: (
      <div
        style={{
          width: "15%",
          marginRight: "0",
          position: "absolute",
          left: "100%",
          height: "100vh",
          backgroundImage: "url(./img/right-arrow.png)",
          backgroundPosition: "center",
          backgroundSize: "contain",
        }}
      ></div>
    ),
  };
  return (
    <div className="motor-slide-img-layout">
      {/* <div className="motor-slide-img-container"></div> */}
      <div style={{ transition: "1s ease" }}>
        {window.outerWidth > 850 ? (
          <Fade {...properties}>
            {imgSlideSrc.map((item, index) => (
              <div className="each-fade">
                <div>
                  <img alt="" src={imgSlideSrc[index]} />
                </div>
              </div>
            ))}
          </Fade>
        ) : (
          <Slide {...properties}>
            {imgSlideSrc.map((item, index) => (
              <div className="each-fade">
                <div>
                  <img alt="" src={imgSlideSrc[index]} />
                </div>
              </div>
            ))}
          </Slide>
        )}
      </div>
    </div>
  );
}
