import React from "react";
import { AboutUs } from "./aboutUsComponent";
import Footer from "../generalLayout/footer";
import { MvSpecialModel } from "./mvSpecialModel";
import Nav from "../generalLayout/nav";
import SlideImgHomepage from "./slideImgHomePage";
import ReactDOM from "react-dom";
import PageName from "../generalLayout/pageName";
const namePage = "MV Agusta Homepage";

export function BodyHomepage() {
  function scrollFunction() {
    if (document.documentElement.scrollTop > window.innerHeight / 3) {
      document
        .getElementsByClassName("mv-nav")[0]
        .classList.add("mv-nav-onScroll");
      for (
        var i = 0;
        i < document.getElementsByClassName("mv-container-div").length;
        i++
      ) {
        document
          .getElementsByClassName("mv-container-div")
          [i].classList.add("mv-container-div-onScroll");
      }
      document.getElementsByClassName("mv-nav-compact")[0].style.display =
        "flex";
      document
        .getElementsByClassName("mv-nav")[0]
        .classList.add("openMainMenu");
      ReactDOM.render(<AboutUs />, document.getElementById("mv-about-us"));
    } else {
      document
        .getElementsByClassName("mv-nav")[0]
        .classList.remove("mv-nav-onScroll");
      for (
        var key = 0;
        key < document.getElementsByClassName("mv-container-div").length;
        key++
      ) {
        document
          .getElementsByClassName("mv-container-div")
          [key].classList.remove("mv-container-div-onScroll");
      }
      document.getElementsByClassName("mv-nav-compact")[0].style.display =
        "none";
      document
        .getElementsByClassName("mv-nav")[0]
        .classList.remove("openMainMenu");
      // ReactDOM.render(<AboutUs />, document.getElementById("mv-about-us"));
    }
  }
  function ScrollMvSpecial() {
    if (document.documentElement.scrollTop > (window.innerHeight * 2) / 2) {
      ReactDOM.render(
        <MvSpecialModel />,
        document.getElementById("mv-special-model")
      );
    }
  }
  function CheckNavCompact() {
    document
      .getElementsByClassName("mv-nav-compact")[0]
      .classList.remove("mv-nav-compact-displayed");
  }
  window.onscroll = function () {
    scrollFunction();
    ScrollMvSpecial();
  };
  return (
    <>
      <PageName namePage={namePage} />
      <Nav />
      <div className="mv-bg" id="main-bg">
        <SlideImgHomepage />
      </div>
      <div className="mv-about-us" id="mv-about-us">
        {/* <AboutUs /> */}
      </div>
      <div className="mv-special-model" id="mv-special-model"></div>
      <div className="mv-special-part">
        <div className="left-part">
          <div className="left-content">
            <a>APPAREL</a>
          </div>
        </div>
        <div className="right-part">
          <div className="right-content">
            <a>SPECIAL PARTS</a>
          </div>
        </div>
      </div>
      <div className="email-container">
        <p className="email-subscribe">SUBSCRIBE TO OUR NEWSLETTER</p>
        <div className="your-email">
          <p>YOUR E-MAIL</p>
        </div>
        <p className="policy" style={{ display: "inline" }}>
          *by sending us e-mail address, you agree with our&nbsp
          <a style={{ display: "inline" }}> privacy policy</a>
        </p>
      </div>
      <Footer />
    </>
  );
}
const NavChosen_Home = () => {
  return (
    <>
      <div className="mv-container-div nav-name-chosen">
        <p className="nav-name">Home</p>
      </div>
    </>
  );
};
window.onload = function () {
  ReactDOM.render(
    <NavChosen_Home />,
    document.getElementsByClassName("mv-nav-container")[1]
  );
  document
    .getElementsByClassName("nav-name-chosen")[0]
    .classList.remove("mv-container-div");
};
