import React, { useEffect } from "react";

export default function Motor3d({
  models
}) {
  var check = 0;
  var oldX = 0;
  var oldX_mobile = 0;
  var idOriginal = "original__";
  var idMod = "mod__";
  function create3dModel(className, idName, imgSrc, location) {
    for (var i = 0; i < 36; i++) {
      var newImg = document.createElement("img");
      newImg.setAttribute("src", "./img/" + imgSrc + "_" + i + ".png");
      newImg.setAttribute("width", "100%");
      newImg.setAttribute("class", className);
      newImg.setAttribute("id", idName + i);
      var drContainer = document.getElementById(location);
      drContainer.appendChild(newImg);
    }
  }
  function renderModel3d() {
    create3dModel(
      "dr-img",
      idOriginal,
      models.original.link,
      "model3d-original"
    );
    create3dModel("dr-img-mod", idMod, models.mod.link, "model3d-mod");
    moveImg(idOriginal, idMod);
  }
  async function moveImg(nameIdOriginal, nameIdMod) {
    document.getElementById(nameIdOriginal + 0).classList.add("dr-img-show");
    document.getElementById(nameIdMod + 0).classList.add("dr-img-show");
    function mouseMove(event) {
      var x = event.pageX;
      // var o= event.clientX;
      if (x > oldX) {
        check++;
        if (check > 35) {
          check = 0;
        }
      } else if (x < oldX) {
        check--;
        if (check < 0) {
          check = 35;
        }
      }
      for (var i = 0; i < 36; i++) {
        document
          .getElementById(nameIdOriginal + i)
          .classList.remove("dr-img-show");
        document.getElementById(nameIdMod + i).classList.remove("dr-img-show");
      }
      document
        .getElementById(nameIdOriginal + check)
        .classList.add("dr-img-show");
      document.getElementById(nameIdMod + check).classList.add("dr-img-show");
      oldX = event.pageX;
    }
    function touchMove(event) {
      var x = event.pageX;
      // var o= event.clientX;
      var o = event.changedTouches[0].pageX;
      if (x > oldX || o > oldX_mobile) {
        check++;
        if (check > 35) {
          check = 0;
        }
      } else if (x < oldX || o < oldX_mobile) {
        check--;
        if (check < 0) {
          check = 35;
        }
      }
      for (var i = 0; i < 36; i++) {
        document
          .getElementById(nameIdOriginal + i)
          .classList.remove("dr-img-show");
        document.getElementById(nameIdMod + i).classList.remove("dr-img-show");
      }
      document
        .getElementById(nameIdOriginal + check)
        .classList.add("dr-img-show");
      document.getElementById(nameIdMod + check).classList.add("dr-img-show");
      oldX = event.pageX;
      oldX_mobile = event.changedTouches[0].pageX;
    }
    document
      .getElementById("dr-container")
      .addEventListener("mousedown", function (event) {
        document.getElementById("dr-container").onmousemove = function (event) {
          mouseMove(event);
        };
      });
    document
      .getElementById("dr-container")
      .addEventListener("mouseup", function (event) {
        document.getElementById("dr-container").onmousemove = null;
      });
    document
      .getElementById("dr-container")
      .addEventListener("touchstart", function (event) {
        document.getElementById("dr-container").ontouchmove = function (event) {
          touchMove(event);
        };
      });
    document
      .getElementById("dr-container")
      .addEventListener("touchend", function (event) {
        document.getElementById("dr-container").ontouchmove = null;
      });
  }
  function openM1() {
    document.getElementById("m-1").classList.add("model3d-i__active");
    document.getElementById("m-2").classList.remove("model3d-i__active");
    document.getElementById("model3d-original").classList.remove("model-hidden");
    document.getElementById("model3d-mod").classList.add("model-hidden");
  }
  function openM2() {
    document.getElementById("m-2").classList.add("model3d-i__active");
    document.getElementById("m-1").classList.remove("model3d-i__active");
    document.getElementById("model3d-original").classList.add("model-hidden");
    document.getElementById("model3d-mod").classList.remove("model-hidden");
  }
  useEffect(() => {
    renderModel3d();
  });
  return (
    <>
      <div className="dr-container" id="dr-container">
        <picture
          id="model3d-original"
          className="model3d"
          style={{
            width: "100%",
            display: "block",
            height: "auto",
            position: "absolute",
          }}
        ></picture>
        <picture
          id="model3d-mod"
          className="model3d model-hidden"
          style={{
            width: "100%",
            display: "block",
            height: "auto",
            position: "absolute",
          }}
        ></picture>
        <div className="model3d-items-lt">
          <div
            className="model3d-i model3d-i__active"
            id="m-1"
            onClick={openM1}
          >
            <span
              className="m-i-l-bt"
              style={{
                background: models.original.colorOriginal,
              }}
            ></span>
            <p className="m-i-l-p">
              DARK METALLIC MATT GREY MAMBA RED MATT CARBON
            </p>
          </div>
          <div className="model3d-i" id="m-2" onClick={openM2}>
            <span
              className="m-i-l-bt"
              style={{
                background: models.mod.colorMod,
              }}
            ></span>
            <p className="m-i-l-p">Racing Kit</p>
          </div>
        </div>
        <div className="scale-lt" style={{width: "100%", height: "300px", background:"red"}}></div>
      </div>
    </>
  );
}
