import React from "react";

export const listimgs = [
  {
    img: "./img/DRA-SCS-performance-min.png",
    content:
      "Super compact, aggressive, stunningly good looking. The design of the Dragster 800 RR takes no prisoners. Emotion in its purest form.",
  },
  {
    img: "./img/DRA-SCS-meccanica-min.png",
    content:
      "The three-cylinder engine was improved to comply with Euro 4 regulation and also to enhance performance and reliability. The engine’s anchoring point to the frame has been revised, as well as the primary gears, valve guides and the cam phasing. The gearbox is also new, quicker and requires less force on the lever to engage gears.",
  },
  {
    img: "./img/DRA-SCS-design-min.png",
    content:
      "The front fender’s side supports are made of aluminium, like the tank damper. The headlight has a new integrated light guide and the tail light is now a single unit.",
  },
  {
    img: "./img/mv-3-years-warranty.jpg",
    content:
      "MV Agusta offers a NEW 3 YEARS WARRANTY on the whole range, with a great service support for all customers. Customize your experience riding a model, discovering special kits and the vast range of readily available spare parts.",
  },
];
export function changeDescription(event,index){
  var x = document.getElementById("model3d-body-left-content");
  x.innerHTML= listimgs[index].content;
  x.style.backgroundImage = "url(listimgs[index].img)";
}

export default function Motor3d({ imgLocate, nameModel, price }) {
  async function renderModel3d() {
    for (var i = 0; i < 36; i++) {
      var newDiv = document.createElement("div");
      newDiv.setAttribute(
        "style",
        "background-image: url(./img/" + imgLocate + "_" + i + ".png)"
      );
      newDiv.setAttribute("class", "dr-img");
      newDiv.setAttribute("id", i);
      var drContainer = document.getElementById("dr-container");
      drContainer.appendChild(newDiv);
    }
    await moveImg();
  }
  async function moveImg() {
    var check = 0;
    var oldX = 0;
    document.getElementById(0).style.opacity = "1";
    function mouseMove(event, y) {
      var x = event.pageX;
      // var o= event.clientX;
      if (x > oldX) {
        check++;
        if (check > 35) {
          check = 0;
        }
      } else if (x < oldX) {
        check--;
        if (check < 0) {
          check = 35;
        }
      }
      for (var i = 0; i < 36; i++) {
        document.getElementById(i).style.opacity = "0";
      }
      document.getElementById(check).style.opacity = "1";
      // document.getElementById("xxx").innerHTML =
      //   "true " + x + " " + y + " " + check;
      console.log("new = " + x + ", " + oldX);
      oldX = event.pageX;
    }

    document
      .getElementById("dr-container")
      .addEventListener("mousedown", function (event) {
        var y = event.pageX;
        document.getElementById("dr-container").onmousemove = function (event) {
          mouseMove(event, y);
        };
      });
    document
      .getElementById("dr-container")
      .addEventListener("mouseup", function (event) {
        document.getElementById("dr-container").onmousemove = null;
      });
    document
      .getElementById("dr-container")
      .addEventListener("touchstart", function (event) {
        var y = event.pageX;
        document.getElementById("dr-container").ontouchmove = function (event) {
          mouseMove(event, y);
        };
      });
    document
      .getElementById("dr-container")
      .addEventListener("touchend", function (event) {
        document.getElementById("dr-container").ontouchmove = null;
      });
  }
  window.onload = () => {
    renderModel3d();
  };
  return (
    <>
      <div className="dr-container" id="dr-container">
        <div className="model3d-name">
          <div className="model3d-inf-top">
            <h4>{nameModel}</h4>
          </div>
          <div className="model3d-inf-bottom">
            <button className="model3d-button">Price: {price}</button>
            <img
              className="rotation-icon"
              alt="click and mouse your mouse to left or right"
              width="75px"
              height="75px"
              src="./img/rotation.png"
            ></img>
            {/* <p>
            Engine: <span>799cc</span>
          </p>
          <p>
            Cylinders: <span>4</span>
          </p>
          <p>
            H-Powers: <span>208Hp</span>
          </p> */}
          </div>
        </div>
      </div>
      <div className="model3d-body">
        <div className="model3d-body-left">
          <h4 id="model3d-body-left-title">
            EMOTIONS
            <br />
            WITHOUT
            <br />
            COMPROMISE
          </h4>
          <p id="model3d-body-left-content">
            Super compact, aggressive, stunningly good looking. The design of
            the Dragster 800 RR takes no prisoners. Emotion in its purest form.
          </p>
          <div className="button-list">
            <button
              className="button-small"
              onClick={(event) => {
                changeDescription(event, 0);
              }}
            ></button>
            <button
              className="button-small"
              onClick={(event) => {
                changeDescription(event, 1);
              }}
            ></button>
            <button
              className="button-small"
              onClick={(event) => {
                changeDescription(event, 2);
              }}
            ></button>
            <button
              className="button-small"
              onClick={(event) => {
                changeDescription(event, 3);
              }}
            ></button>
          </div>
        </div>
        <div
          className="model3d-body-right"
          style={{ backgroundImage: "url(./img/DRA-SCS-performance-min.png)" }}
        >
          <div className="model3d-body-side"></div>
        </div>
      </div>
    </>
  );
}
