import React from "react";
import ReactDOM from "react-dom";
import { AboutUs } from "../bodyHomePage/aboutUsComponent";
import { MvSpecialModel } from "../bodyHomePage/mvSpecialModel";
export function scrollFunction() {
  if (document.documentElement.scrollTop > 100) {
    document
      .getElementsByClassName("mv-nav")[0]
      .classList.add("mv-nav-onScroll");
    document.getElementsByClassName("mv-nav-compact")[0].style.display = "flex";
    document.getElementsByClassName("mv-nav")[0].classList.add("openMainMenu");
  }else{
    document
      .getElementsByClassName("mv-nav")[0]
      .classList.remove("mv-nav-onScroll");
  }
  if (document.documentElement.scrollTop > window.innerHeight / 3) {
    for (
      var i = 0;
      i < document.getElementsByClassName("mv-container-div").length;
      i++
    ) {
      document
        .getElementsByClassName("mv-container-div")
        [i].classList.add("mv-container-div-onScroll");
    }
    ReactDOM.render(<AboutUs />, document.getElementById("mv-about-us"));
  } else {
    for (
      var key = 0;
      key < document.getElementsByClassName("mv-container-div").length;
      key++
    ) {
      document
        .getElementsByClassName("mv-container-div")
        [key].classList.remove("mv-container-div-onScroll");
    }
    document.getElementsByClassName("mv-nav-compact")[0].style.display = "none";
    document
      .getElementsByClassName("mv-nav")[0]
      .classList.remove("openMainMenu");
    // ReactDOM.render(<AboutUs />, document.getElementById("mv-about-us"));
  }
}
export function ScrollMvSpecial() {
  if (document.documentElement.scrollTop > (window.innerHeight * 2) / 1.8) {
    ReactDOM.render(
      <MvSpecialModel />,
      document.getElementById("mv-special-model")
    );
  }
}
export function CheckNavCompact() {
  document
    .getElementsByClassName("mv-nav-compact")[0]
    .classList.remove("mv-nav-compact-displayed");
  console.log("aaaaaa");
}
