import React, { useEffect, useState } from "react";
import ReactDOM from "react-dom";
import { Fade } from "react-slideshow-image";
import { Spring } from "react-spring/renderprops";
import "react-slideshow-image/dist/styles.css";
export default function SlideImgHomepage() {
  const [widthOfDevice, setWidthOfDevice] = useState("");
  const imgSrc = ["img/bg1.jpg", "img/bg2.jpg", "img/bg3.jpg"];
  const slideImgContents = [
    ["DISCOVER", '"LE"', "BIKE1"],
    ["DISCOVER", '"LE"', "BIKE2"],
    ["DISCOVER", '"LE"', "BIKE3"],
  ];
  const properties = {
    duration: 3500,
    transitionDuration: 500,
    infinite: true,
    indicators: true,
    canSwipe: true,
    scale: 0.4,
    pauseOnHover: false,
    indicators: (i) => <div className="indicator"></div>,
    prevArrow: (
      <div
        style={{
          width: "300px",
          marginRight: "0",
          position: "absolute",
          left: 0,
          height: "100vh",
          display: "none",
        }}
      ></div>
    ),
    nextArrow: (
      <div
        style={{
          width: "300px",
          marginRight: "0",
          position: "absolute",
          right: 0,
          height: "100vh",
          display: "none",
        }}
      ></div>
    ),
  };

  useEffect(() => {
    ReactDOM.render(<SlideImgHomepage />, document.getElementById("main-bg"));
  }, [widthOfDevice]);
  window.addEventListener("resize", reportWindowSize);
  function reportWindowSize() {
    setWidthOfDevice(window.innerWidth);
  }
  if (window.outerWidth > 750) {
    return (
      <>
        <div style={{ transition: "1s ease" }}>
          <Spring from={{opacity: 0}} to={{opacity: 0}}>
            {(props) => (
              <Fade {...properties} style={props}>
                {imgSrc.map((item, index) => (
                  <div className="each-fade">
                    <div>
                      <div className="slideImgContentLayout">
                        <div className="slideImgContentLayout_top">
                          <img
                            className="img-bg-title"
                            alt=""
                            src="./img/homepage/sv-alpine-logo.svg"
                          ></img>
                        </div>
                        <div className="slideImgContentLayout_center">
                          {slideImgContents[index].map(
                            (slideImgContent, key) => (
                              <Spring
                                from={{
                                  opacity: 0,
                                  position: "relative",
                                  top: 100,
                                  transition: "0.25s ease",
                                }}
                                to={{
                                  opacity: 1,
                                  top: 0,
                                }}
                              >
                                {(props) => (
                                  <div className="sp-ly">
                                    <p style={props}>{slideImgContent}</p>
                                  </div>
                                )}
                              </Spring>
                            )
                          )}
                          <button type="button">
                            <a>LEARN MORE</a>
                          </button>
                        </div>
                      </div>
                      <img src={imgSrc[index]} />
                    </div>
                  </div>
                ))}
              </Fade>
            )}
          </Spring>
        </div>
      </>
    );
  } else {
    return (
      <>
        <div
          style={{
            width: "100%",
            height: "100%",
            backgroundImage: `url(./${imgSrc[0]})`,
          }}
        ></div>
      </>
    );
  }
}
