import React from "react";

export default function Motor3d({ imgOriginalLocation, imgModLocation, nameModel, price }) {
  var check = 0;
  var oldX = 0;
  var oldX_mobile = 0;
  var idOriginal = "original__";
  var idMod = "mod__";
  function create3dModel(className, idName, imgSrc, location) {
    for (var i = 0; i < 36; i++) {
      var newImg = document.createElement("img");
      newImg.setAttribute("src", "./img/" + imgSrc + "_" + i + ".png");
      newImg.setAttribute("width", "100%");
      newImg.setAttribute("class", className);
      newImg.setAttribute("id", idName + i);
      var drContainer = document.getElementById(location);
      drContainer.appendChild(newImg);
    }
  }
  async function renderModel3d() {
    create3dModel("dr-img", idOriginal, imgOriginal, "model3d-original");
    create3dModel("dr-img-mod", idMod, );
    await moveImg(idMod);
  }
  async function moveImg(nameOfID) {
    document.getElementById(nameOfID + 0).style.opacity = "1";
    function mouseMove(event) {
      var x = event.pageX;
      // var o= event.clientX;
      if (x > oldX) {
        check++;
        if (check > 35) {
          check = 0;
        }
      } else if (x < oldX) {
        check--;
        if (check < 0) {
          check = 35;
        }
      }
      for (var i = 0; i < 36; i++) {
        document.getElementById(nameOfID + i).style.opacity = "0";
      }
      document.getElementById(nameOfID + check).style.opacity = "1";
      // document.getElementById("xxx").innerHTML =
      //   "true " + x + " " + y + " " + check;
      console.log("new = " + x + ", " + oldX);
      oldX = event.pageX;
    }
    function touchMove(event) {
      var x = event.pageX;
      // var o= event.clientX;
      var o = event.changedTouches[0].pageX;
      if (x > oldX || o > oldX_mobile) {
        check++;
        if (check > 35) {
          check = 0;
        }
      } else if (x < oldX || o < oldX_mobile) {
        check--;
        if (check < 0) {
          check = 35;
        }
      }
      for (var i = 0; i < 36; i++) {
        document.getElementById(i).style.opacity = "0";
      }
      document.getElementById(check).style.opacity = "1";
      oldX = event.pageX;
      oldX_mobile = event.changedTouches[0].pageX;
    }
    document
      .getElementById("dr-container")
      .addEventListener("mousedown", function (event) {
        document.getElementById("dr-container").onmousemove = function (event) {
          mouseMove(event);
        };
      });
    document
      .getElementById("dr-container")
      .addEventListener("mouseup", function (event) {
        document.getElementById("dr-container").onmousemove = null;
      });
    document
      .getElementById("dr-container")
      .addEventListener("touchstart", function (event) {
        var y = event.pageX;
        document.getElementById("dr-container").ontouchmove = function (event) {
          touchMove(event, y);
        };
      });
    document
      .getElementById("dr-container")
      .addEventListener("touchend", function (event) {
        document.getElementById("dr-container").ontouchmove = null;
      });
  }
  window.onload = () => {
    renderModel3d();
  };
  return (
    <>
      <div className="dr-container" id="dr-container">
        <picture
          id="model3d-original"
          style={{ width: "100%", display: "block", height: "auto" }}
        ></picture>
        <picture
          id="model3d-mod"
          style={{ width: "100%", display: "block", height: "auto" }}
        ></picture>
      </div>
    </>
  );
}
