import React, { useEffect, useState } from "react";
import ReactDOM from "react-dom";
import { Fade } from "react-slideshow-image";
import { Spring } from "react-spring/renderprops";
import "react-slideshow-image/dist/styles.css";
export default function SlideImgHomepage() {
  const [widthOfDevice, setWidthOfDevice] = useState("");
  const bgColor = [
    "linear-gradient(90deg, #00213D 0%, #020308 100%)",
    "linear-gradient(90deg, #00213D 0%, #020308 100%)",
    "linear-gradient(90deg, #00213D 0%, #020308 100%)",
  ];
  const imgSrc = [
    "img/homepage/reveal-xl.webp",
    "img/homepage/superveloce-alpine-xl.png",
    "img/homepage/superveloce-75-xl.png",
    "img/homepage/rush-1000-xl.png",
  ];
  const imgTitle = [
    "",
    "img/homepage/sv-alpine-logo.svg",
    "img/homepage/superveloce-75-anniversario.svg",
    "img/homepage/rush1000-logo.svg",
  ];
  const slideImgContents = [
    ["2021", "REVEAL"],
    ["DISCOVER", '"LE"', "BIKE"],
    ["CELEBRATE", "THE", "POWER"],
    ["LIMITED", "SERIES", "BEAST", "MODE"],
  ];
  const colorContentTitle=["white","white", "white", "black"];
  const heightTitle = ["0%","34%", "34%", "26%"];
  const properties = {
    duration: 4000,
    transitionDuration: 500,
    infinite: true,
    // indicators: true,
    canSwipe: true,
    scale: 0.4,
    pauseOnHover: false,
    // indicators: (i) => <div className="indicator"></div>,
    prevArrow: (
      <div
        style={{
          width: "300px",
          marginRight: "0",
          position: "absolute",
          left: 0,
          height: "100vh",
          display: "none",
        }}
      ></div>
    ),
    nextArrow: (
      <div
        style={{
          width: "300px",
          marginRight: "0",
          position: "absolute",
          right: 0,
          height: "100vh",
          display: "none",
        }}
      ></div>
    ),
  };

  useEffect(() => {
    ReactDOM.render(<SlideImgHomepage />, document.getElementById("main-bg"));
  }, [widthOfDevice]);
  window.addEventListener("resize", reportWindowSize);
  function reportWindowSize() {
    setWidthOfDevice(window.innerWidth);
  }
  // if (window.outerWidth > 750) {
    return (
      <>
        <div style={{ transition: "1s ease" }}>
          <Spring from={{ opacity: 0 }} to={{ opacity: 1 }}>
            {(props) => (
              <Fade {...properties} style={props}>
                {imgSrc.map((item, index) => (
                  <div
                    className="each-fade"
                    style={{ background: bgColor[index] }}
                  >
                    <div>
                      <div className="slideImgContentLayout">
                        <div
                          className="slideImgContentLayout_top"
                          style={{ height: heightTitle[index] }}
                        >
                          <div
                            className="img-bg-title"
                            style={{
                              backgroundImage: "url(./" + imgTitle[index] + ")",
                            }}
                          ></div>
                        </div>
                        <div className="slideImgContentLayout_center">
                          {slideImgContents[index].map(
                            (slideImgContent) => (
                              <Spring
                                from={{
                                  opacity: 0,
                                  position: "relative",
                                  top: 100,
                                  transition: "0.25s ease",
                                }}
                                to={{
                                  opacity: 1,
                                  top: 0,
                                  position: "relative",
                                }}
                              >
                                {(props) => (
                                  <div
                                    className="sp-ly"
                                    style={{ color: colorContentTitle[index] }}
                                  >
                                    <p style={props}>{slideImgContent}</p>
                                  </div>
                                )}
                              </Spring>
                            )
                          )}
                          <div className="bt-layout">
                            <button
                              type="button"
                              id={"bt-tt-" + index}
                              className="bt-tt-sl"
                            >
                              <a href>LEARN MORE</a>
                            </button>
                          </div>
                        </div>
                      </div>
                      <img alt="" src={item} />
                    </div>
                  </div>
                ))}
              </Fade>
            )}
          </Spring>
        </div>
      </>
    );
}
