import React, { useState, useEffect } from "react";
import { Spring } from "react-spring/renderprops";
const manifesto_Arr = ["THE", "SMART", "STREET", "FIGHTER"];
export default function MotorPageHeader() {
  const [manifesto, setManifesto] = useState(manifesto_Arr);
  useEffect(() => {
  for (var i = 0; i < manifesto_Arr.length; i++) {
    document.getElementsByClassName("motorPageHeader-manifesto")[
      i
    ].style.height = (1 / manifesto_Arr.length) * 100 +"%";
  }
  });

  return (
    <>
      <div
        className="motorPageHeader"
        style={{
          backgroundImage: "url(./img/dragster800rrscs/dragster-800-rr-xl.png)",
          position: "relative",
        }}
      >
        <div className="motorPageHeader-Content">
          <div
            className="motorPageHeader-Content-top"
            style={{
              backgroundImage:
                "url(./img/dragster800rrscs/dragster-800-rr-scs.svg)",
            }}
          ></div>
          <div className="motorPageHeader-Content-center">
            <div className="motorPageHeader-Content-center-top">
              {manifesto_Arr.map((item, index) => (
                <Spring
                  from={{
                    opacity: 0,
                    marginTop: 10,
                    marginBottom: "1px solid black",
                  }}
                  to={{ opacity: 1, marginTop: 0, marginBottom: 0 }}
                >
                  {(props) => (
                    <div className="motorPageHeader-manifesto" style={props}>
                      {manifesto_Arr[index]}
                    </div>
                  )}
                </Spring>
              ))}
            </div>
            <div className="motorPageHeader-Content-center-bottom">
              <p>
                Dragster exudes exuberance and character. Born to inspire and
                ready to burn the tarmac, effortlessly and carefree thanks to
                its smart shifting system.
              </p>
            </div>
          </div>
          <div className="motorPageHeader-Content-bottom">
            <p>Starting from 19.490 â‚¬</p>
          </div>
        </div>
      </div>
    </>
  );
}
