import React, { useState, useEffect } from "react";
import { Spring } from "react-spring/renderprops";

export default function MotorPageHeader({
  manifesto_Arr,
  nameHeaderTitle,
  colorBg,
  contentHeaderContent,
}) {
  useEffect(() => {
    for (var i = 0; i < manifesto_Arr.length; i++) {
      document.getElementsByClassName("motorPageHeader-manifesto")[
        i
      ].style.height = (1 / manifesto_Arr.length) * 100 + "%";
    }
  });

  return (
    <>
      <div
        className="motorPageHeader"
        style={{
          // backgroundImage: "url(./img/dragster800rrscs/dragster-800-rr-xl.png)",
          backgroundColor: `${colorBg}`,
          position: "relative",
        }}
      >
        <div className="motorPageHeader-Content" style={{ zIndex: 10 }}>
          {/* <div className="motorPageHeader-Content" style={{left: 0}}> */}

          <Spring
            from={{
              opacity: 0,
              transition: "0.4s ease",
              backgroundImage: "url(./img/" + nameHeaderTitle + ")",
            }}
            to={{
              opacity: 1,
              backgroundImage: "url(./img/" + nameHeaderTitle + ")",
            }}
          >
            {(props) => (
              <div
                className="motorPageHeader-Content-top"
                style={
                  props
                  // backgroundImage: "url(./img/" + nameHeaderTitle + ")"
                }
              ></div>
            )}
          </Spring>
          <div className="motorPageHeader-Content-center">
            <div className="motorPageHeader-Content-center-top">
              {manifesto_Arr.map((item, index) => (
                <Spring
                  from={{
                    opacity: 0,
                    position: "relative",
                    top: 100,
                    transition: "0.25s linear",
                  }}
                  to={{
                    opacity: 1,
                    top: 0,
                  }}
                >
                  {(props) => (
                    <div className="motorPageHeader-manifesto">
                      <p style={props}>{manifesto_Arr[index]}</p>
                    </div>
                  )}
                </Spring>
              ))}
            </div>
            <Spring
              from={{ opacity: 0, transition: "0.4s ease" }}
              to={{ opacity: 1 }}
            >
              {(props) => (
                <div
                  className="motorPageHeader-Content-center-bottom"
                  style={props}
                >
                  <p></p>
                </div>
              )}
            </Spring>
          </div>
          <Spring
            from={{ opacity: 0, transition: "0.4s ease" }}
            to={{ opacity: 1 }}
          >
            {(props) => (
              <div className="motorPageHeader-Content-bottom" style={props}>
                <p>Starting from 19.490 €</p>
              </div>
            )}
          </Spring>
        </div>
        <div
          className="xxxxx"
          style={{
            backgroundImage:
              "url(./img/dragster800rrscs/dragster-800-rr-xl.png",
            width: "100%",
            height: "100vh",
            position: "sticky",
            top: 0,
            backgroundPosition: "center",
            backgroundSize: "cover",
          }}
        ></div>
      </div>
    </>
  );
}
