import React, { useEffect, useState } from "react";
import { Fade, Slide } from "react-slideshow-image";
import "react-slideshow-image/dist/styles.css";

export default function MotorSlideImg() {
  const [size, setSize] = useState((window.outerWidth> 850)? false : true);
  const imgSrc = ["img/bg1.jpg", "img/bg2.jpg", "img/bg3.jpg"];
  window.onresize = () => {
    if (window.outerWidth > 850) {
      setSize(false);
    } else {
      setSize(true)
    }
  };
  const properties = {
    autoplay: false,
    duration: 3500,
    transitionDuration: 500,
    infinite: true,
    indicators: true,
    canSwipe: size,
    scale: 0.4,
    pauseOnHover: false,
    indicators: (i) => <div className="indicator"></div>,
    prevArrow: (
      <div
        style={{
          width: "15%",
          marginRight: "0",
          position: "absolute",
          right: "100%",
          height: "100vh",
          backgroundImage: "url(./img/left-arrow.png)",
          backgroundPosition: "center",
          backgroundSize: "contain",
        }}
      ></div>
    ),
    nextArrow: (
      <div
        style={{
          width: "15%",
          marginRight: "0",
          position: "absolute",
          left: "100%",
          height: "100vh",
          backgroundImage: "url(./img/right-arrow.png)",
          backgroundPosition: "center",
          backgroundSize: "contain",
        }}
      ></div>
    ),
  };
  return (
    <div className="motor-slide-img-layout">
      {/* <div className="motor-slide-img-container"></div> */}
      <div style={{ transition: "1s ease" }}>
        <Slide {...properties}>
          {imgSrc.map((item, index) => (
            <div className="each-fade">
              <div>
                <img src={imgSrc[index]} />
              </div>
            </div>
          ))}
        </Slide>
      </div>
    </div>
  );
}
